import React from "react";
import { Field, reduxForm } from "redux-form";
import { compose } from "redux";
import {
  renderFormGroupField,
  renderFormTextArea,
} from "../helpers/renderForm";
import {
  required,
  maxLength50,
  name,
  maxLengthMobileNo,
  minLengthMobileNo,
  digit,
  email,
  maxLength1000,
} from "../helpers/validation";
const ContactUsForm = (props) => {
  const { handleSubmit, submitting, onSubmit, submitFailed } = props;
  return (
    <form
      onSubmit={handleSubmit(onSubmit)}
      className={`needs-validation ${submitFailed ? "was-validated" : ""}`}
      noValidate

    >
      <div className="row">
        <div className="col-md-12">
          <Field
            name="name"
            type="text"
            label="Name"
            component={renderFormGroupField}
            placeholder="Your full name"
            required={true}
            validate={[required, maxLength50, name]}
            pattern="[a-zA-Z\s]*"
            maxLength="50"
            className="mb-3"
          />
        </div>
      </div>
      <div className="row">
        <div className="col-md-6">
          <Field
            name="mobileNo"
            type="String"
            label="Mobile No."
            component={renderFormGroupField}
            placeholder="Mobile no with country code"
            validate={[required, maxLengthMobileNo, minLengthMobileNo, digit]}
            required={true}
            pattern="[0-9]{10,15}"
            max="15"
            min="10"
            className="mb-3"
          />
        </div>
        <div className="col-md-6">
          <Field
            name="email"
            type="email"
            label="Email address"
            component={renderFormGroupField}
            placeholder="Your email address"
            validate={[required, maxLength50, email]}
            maxLength="50"
            required={true}
            className="mb-3"
          />
        </div>
      </div>
      <div className="row g-3">
        <div className="col-md-12">
          <Field
            name="message"
            label="Message"
            maxLength="1000"
            component={renderFormTextArea}
            validate={maxLength1000}
            placeholder="What you are looking for?"
          />
        </div>
      </div>
      <div className="d-grid" style={{marginTop:'14px'}}>
        <button
          type="submit"
          className="btn btn-primary mt-3"
          disabled={submitting}
          style={{backgroundColor:"black", border:"1px solid black"}}
        >
          Submit
        </button>
      </div>
    </form>
  );
};
export default compose(
  reduxForm({
    form: "demo1",
  })
)(ContactUsForm);
