import React, { useState, useEffect } from "react";
import { getCart, getProduct, getCustomerData, checkOut  } from "../../api.js";
import { Link } from "react-router-dom";
import {
  required,
  maxLengthMobileNo,
  minLengthMobileNo,
  digit
} from "../../helpers/validation";
const CheckoutView = (props) => {
  const { handleSubmit, submitting, onSubmit, submitFailed } = props;
  const [quantities, setQuantities] = useState();
  const [cart, setCart] = useState([]);
  const [customerData, setCustomerData] = useState({});

  useEffect(() => {
    fetchCartData();
  }, []);
  const fetchCartData = async () => {
    try {
      const userData = localStorage.getItem('data');
      const cartData = await getCart(userData);
      const initialQuantities = cartData.map(() => 1);
      setQuantities(initialQuantities);
      const customerInfo = await getCustomerData(userData);
      setCustomerData(customerInfo);
      const updatedCart = await Promise.all(cartData.map(async (item) => {
        const productDetails = await getProduct(item.productId);
        return {
          ...item,
          image: productDetails.image 
        };
      }));
      setCart(updatedCart);
      console.log(cart);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };
  const handleSubmitForm = async (e) => {
    e.preventDefault();
    try {
      const orderData = {
        customer: customerData,
        cart: cart.map(item => ({ productId: item.productId, quantity: item.quantity, size: item.size }))
      };
      const response = await checkOut(orderData);
      console.log("Order submitted successfully:", response);
    } catch (error) {
      console.error("Error submitting order:", error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmitForm}>
        <div className="bg-secondary border-top p-4 text-white mb-3">
          <h1 className="display-6 text-center">Checkout</h1>
        </div>
        <div className="container mb-3">
          <div className="row">
            <div className="col-md-8">
              <div className="card mb-3" style={{ borderColor: "black" }}>
                <div className="card-header" style={{ backgroundColor: "black", color: "white" }}>
                  <i className="bi bi-envelope"></i> Contact Info
                </div>
                <div className="card-body">
                  <div className="row g-3">
                    <div className="col-md-6">
                      <input
                        type="email"
                        className="form-control"
                        placeholder="Registered email address"
                        aria-label="Email Address"
                        value={customerData.email}
                        onChange={(e) => setCustomerData({...customerData, email: e.target.value})}
                        validate={[required]}
                        required={true}
                        pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$"
                        title="Please enter your valid email address"
                      />
                    </div>
                    <div className="col-md-6">
                      <input
                        type="tel"
                        className="form-control"
                        placeholder="Registered mobile no. without country code"
                        aria-label="Mobile no"
                        value={customerData.phno}
                        onChange={(e) => setCustomerData({...customerData, phno: e.target.value})}
                        pattern="^[0-9]{10}$"
                        title="Please enter your valid mobile number"
                        validate={[required, maxLengthMobileNo, minLengthMobileNo, digit]}
                        required={true}
                        max="9999999999"
                        min="9999999999"
                      />
                    </div>
                  </div>
                </div>
              </div>
              <div className="card mb-3" style={{ borderColor: "black" }}>
                <div className="card-header" style={{ backgroundColor: "black", color: "white" }}>
                  <i className="bi bi-truck"></i> Shipping Info
                </div>
                <div className="card-body">
                  <div className="row g-3">
                    <div className="col-md-12">
                      <input
                        type="text"
                        className="form-control"
                        placeholder="Name"
                        required
                      />
                    </div>
                    <div className="col-md-6">
                      <input
                        type="text"
                        className="form-control"
                        placeholder="Addresss 1"
                        required
                      />
                    </div>
                    <div className="col-md-6">
                      <input
                        type="text"
                        className="form-control"
                        placeholder="Address 2 (Optional)"
                      />
                    </div>
                    <div className="col-md-4">
                      <select className="form-select" required>
                        <option value>City</option>
                        <option>Asansol</option>
                        <option>Howrah</option>
                        <option>Kolkata</option>
                        <option>Malda</option>
                        <option>Raiganj</option>
                        <option>Salt Lake</option>
                      </select>
                    </div>
                    <div className="col-md-4">
                      <input
                        type="text"
                        className="form-control"
                        placeholder="Zip"
                        required
                      />
                    </div>
                    <div className="col-md-4">
                      <input
                        type="text"
                        className="form-control"
                        placeholder="Landmark"
                        required
                      />
                    </div>
                  </div>
                </div>
              </div>
              <div className="card mb-3" style={{ borderColor: "black" }}>
                <div className="card-header" style={{ backgroundColor: "black", color: "white" }}>
                  <i className="bi bi-credit-card-2-front"></i> Payment Method
                </div>
                <div className="card-body">
                  <input type="radio" id="option1" name="options" value="option1" />
                  <label for="option1" style={{ paddingLeft: "15px" }}>Cash On Delivery</label>
                </div>
              </div>
            </div>
            <div className="col-md-4">
              <div className="card" style={{ borderColor: "black" }}>
                <div className="card-header" style={{ backgroundColor: "black", color: "white" }}>
                  <i className="bi bi-cart3"></i> Cart{" "}
                  <span className="badge bg-secondary float-end">{cart.length}</span>
                </div>
                <ul className="list-group list-group-flush">
                  {cart.map((item, index) => (
                    <li key={index} className="list-group-item d-flex justify-content-between lh-sm">
                      <div>
                        <p className="my-0">{item.productName}</p>
                        <small className="text-muted">Size : {item.size} Quantity: {item.quantity}</small>
                      </div>
                      <span>₹ {item.price * item.quantity}</span> 
                    </li>
                  ))}
                  <li className="list-group-item d-flex justify-content-between">
                    <span><h6>Total</h6> </span>
                    <strong>₹ {cart.reduce((total, item) => total + item.price * item.quantity, 0)}</strong>
                  </li>
                </ul>
              </div>
            </div>
          </div>
        </div>
        <div className="container mb-3">
          <div className="row">
            <div className="col-md-7" style={{textAlign:'center'}}>
              <button
                type="submit"
                className="btn btn-dark mb-3"
                disabled={submitting}
                style={{ background: "black", borderColor: "black" }}
              >
                Order
              </button>
            </div>
            </div>
            </div>
      </form>
    </div>
  );
};

export default CheckoutView;
