import { Link } from "react-router-dom";
import React, { useState } from "react";
import {
  required,
  maxLengthMobileNo,
  minLengthMobileNo,
  digit
} from "../../helpers/validation";
import { checkOut } from "../../api.js";
const initialValues = {
  name: "",
  email: "",
  phno: "",
  address1: "",
  address2: "",
  city: "",
  zip: "",
  land: "",
  payment: ""
};
const CheckoutView = (props) => {
  const [isError, setIsError] = useState(false);
  const [isCheckout, setIsCheckout] = useState(false);
  const [isEmailNotExists, setIsEmailNotExists] = useState(false);
  const [checkout, setCheckout] = useState(initialValues); 
  const onValueChange = (e) => {
    setCheckout({ ...checkout, [e.target.name]: e.target.value });
    console.log(checkout)
  };
  const addCheckout = async () => {
    const allFieldsFilled = Object.values(checkout).every(value => value.trim() !== '');
    if (!allFieldsFilled) {
      return;
    }
    try {
      await checkOut(checkout); 
      setIsCheckout(true);
      setIsError(false);
      setIsEmailNotExists(false);
      setTimeout(() => {
        setIsCheckout(false);
        props.reset();
      }, 2000);
    } catch (error) {
      setIsCheckout(false);
      if (error.message === 'Email does not exist') {
        setTimeout(() => {
      setIsEmailNotExists(false);
      props.reset();
    }, 2000);
  } else {
    setTimeout(() => {
      setIsError(true);
      props.reset();
        }, 2000);
    }
  }
};
  const { handleSubmit, submitting, submitFailed } = props;
  return (
    <div>
      <form
        onSubmit={handleSubmit(addCheckout)}
        className={`needs-validation ${submitFailed ? "was-validated" : ""}`}
        noValidate
      >
      <div className="bg-secondary border-top p-4 text-white mb-3">
        <h1 className="display-6 text-center">Checkout</h1>
      </div>
      <div className="container mb-3">
        <div className="row">
          <div className="col-md-8">
            <div className="card mb-3" style = {{borderColor : "black"}}>
              <div className="card-header" style = {{backgroundColor : "black", color : "white"}}>
                <i className="bi bi-envelope"></i> Contact Info
              </div>
              <div className="card-body">
                <div className="row g-3">
                  <div className="col-md-6">
                    <input
                    name="email"
                      type="email"
                      className="form-control"
                      placeholder="Registered email address"
                      aria-label="Email Address"
                      validate={[required]}
                      required={true}
                      pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$"
                      title="Please enter your registered email address"
                      onChange={(e) => onValueChange(e)}
                    />
                  </div>
                  <div className="col-md-6">
                    <input
                    name="phno"
                      type="tel"
                      className="form-control"
                      placeholder="Registered mobile no. without country code"
                      aria-label="Mobile no"
                      pattern= "^[0-9]{10}$"
                      title="Please enter your valid mobile number"
                      validate={[required, maxLengthMobileNo, minLengthMobileNo, digit]}
                      required={true}
                      max="9999999999"
                      min="9999999999"
                      onChange={(e) => onValueChange(e)}
                    />
                  </div>
                </div>
              </div>
            </div>
            <div className="card mb-3" style = {{borderColor : "black"}}>
              <div className="card-header" style = {{backgroundColor : "black", color : "white"}}>
                <i className="bi bi-truck"></i> Shipping Info
              </div>
              <div className="card-body">
                <div className="row g-3">
                  <div className="col-md-12">
                    <input
                    name="name"
                      type="text"
                      className="form-control"
                      placeholder="Name"
                      required
                      onChange={(e) => onValueChange(e)}
                    />
                  </div>
                  <div className="col-md-6">
                    <input
                    name="address1"
                      type="text"
                      className="form-control"
                      placeholder="Addresss 1"
                      required
                      onChange={(e) => onValueChange(e)}
                    />
                  </div>
                  <div className="col-md-6">
                    <input
                    name="address2"
                      type="text"
                      className="form-control"
                      placeholder="Address 2 (Optional)"
