import React, { useState, useEffect } from "react";
import { BrowserRouter, Route, Routes, useLocation } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.js";
import "bootstrap-icons/font/bootstrap-icons.css";
import "./App.min.css";
import Header from "./components/Header";
import Footer from "./components/Footer";
import HomeView from "./views/Home";
import TopMenu from "./components/TopMenu"; // Import TopMenu
import OrdersView from "./views/account/Orders"; // Import OrdersView
import SignInAdminView from "./views/account/SignInAdmin";
import AddProductView from "./views/account/AddProduct";
import AdminDashView from "./views/account/AdminDash";
import ManageOrderView from "./views/account/ManageOrder";
import ReturnOrderView from "./views/account/ReturnOrder";
import AdminContactUsView from "./views/account/AdminContactUs";
import SignInView from "./views/account/SignIn";
import SignUpView from "./views/account/SignUp";
import ForgotPasswordView from "./views/account/ForgotPassword";
import MyProfileView from "./views/account/MyProfile";
import Lis1 from "./views/product/List1";
import ProductListView2 from "./views/product/List2";
import ProductListView3 from "./views/product/List3";
import ProductDetailView from "./views/product/Detail";
import CartView from "./views/cart/Cart";
import FailView from "./views/cart/Fail";
import SuccessView from "./views/cart/Success";
import CheckoutView from "./views/cart/Checkout";
import NotFoundView from "./views/pages/404";
import InternalServerErrorView from "./views/pages/500";
import ContactUsView from "./views/pages/ContactUs";
import PaymentView from "./views/pages/Payment";
import PrivacyView from "./views/pages/Privacy";
import TeamView from "./views/pages/Team";
import SecurityPolicyView from "./views/pages/Security";
import ReturnView from "./views/pages/Return";
import AboutView from "./views/pages/About";
import TermsView from "./views/pages/TermsOfUse";
import FaqView from "./views/pages/FAQ";

function App() {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Simulate loading time
    setTimeout(() => {
      setIsLoading(false);
    }, 2000);
  }, []);

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<HeaderFooterHandler />} />
      </Routes>
    </BrowserRouter>
  );
}

function HeaderFooterHandler() {
  const location = useLocation();
  const hideHeaderFooterRoutes = [
    "/account/signinadmin",
    "/account/addproduct",
    "/account/admindash",
    "/account/manageorder",
    "/account/returnorder",
    "/account/admincontactus",
  ];

  if (hideHeaderFooterRoutes.includes(location.pathname)) {
    return (
      <>
        <Routes>
          <Route path="/account/signinadmin" element={<SignInAdminView />} />
          <Route path="/account/addproduct" element={<AddProductView />} />
          <Route path="/account/admindash" element={<AdminDashView />} />
          <Route path="/account/manageorder" element={<ManageOrderView />} />
          <Route path="/account/returnorder" element={<ReturnOrderView />} />
          <Route path="/account/admincontactus" element={<AdminContactUsView />} />
        </Routes>
      </>
    );
  }

  return (
    <>
      <Header />
      <TopMenu /> {/* Render TopMenu component */}
      <Routes>
        <Route path="/" element={<HomeView />} />
        <Route path="/account/signin" element={<SignInView />} />
        <Route path="/account/signup" element={<SignUpView />} />
        <Route path="/account/forgotpassword" element={<ForgotPasswordView />} />
        <Route path="/account/myprofile" element={<MyProfileView />} />
        <Route path="/account/orders" element={<OrdersView />} /> {/* Render OrdersView component */}

        <Route path="/category-1" element={<ProductListView1 />} />
        <Route path="/category-2" element={<ProductListView2 />} />
        <Route path="/category-3" element={<ProductListView3 />} />
        <Route path="/product/detail" element={<ProductDetailView />} />
        <Route path="/cart" element={<CartView />} />
        <Route path="/fail" element={<FailView />} />
        <Route path="/success" element={<SuccessView />} />
        <Route path="/about" element={<AboutView />} />
        <Route path="/terms" element={<TermsView />} />
        <Route path="/return" element={<ReturnView />} />
        <Route path="/payment" element={<PaymentView />} />
        <Route path="/privacy" element={<PrivacyView />} />
        <Route path="/security" element={<SecurityPolicyView />} />
        <Route path="/Team" element={<TeamView />} />
        <Route path="/checkout" element={<CheckoutView />} />
        <Route path="/contact-us" element={<ContactUsView />} />
        <Route path="/500" element={<InternalServerErrorView />} />
        <Route path="/FAQ" element={<FaqView />} />
        <Route path="*" element={<NotFoundView />} />
      </Routes>
      <Footer />
    </>
  );
}

export default App;
