import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { getCart, getProduct } from "../../api.js";
const CartView = () => {
  const [quantities, setQuantities] = useState([1, 1]);
  const [prods, setProds] = useState([]);
  const [loading, setLoading] = useState(true);
  const [cart, setCart] = useState([]);
  useEffect(() => {
    fetchCartData();
  }, []);
  const fetchCartData = async () => {
    try {
      const userData = localStorage.getItem('data');
      const cartData = await getCart(userData);
      setCart(cartData);
      console.log(cart);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };
  useEffect(() => {
    fetchData();
  }, []);
  const fetchData = async () => {
    try {
      const productPromises = cart.map(item => getProduct(item.productId));
      const products = await Promise.all(productPromises);
      setProds(products);
      setLoading(false); 
  } catch (error) {
    console.error('Error fetching product data:', error);
    setLoading(false); 
  }
};

  const decreaseQuantity = (index) => {
    if (quantities[index] > 1) {
      const newQuantities = [...quantities];
      newQuantities[index] = newQuantities[index] - 1;
      setQuantities(newQuantities);
    }
  };
  const increaseQuantity = (index) => {
    const newQuantities = [...quantities];
    newQuantities[index] = newQuantities[index] + 1;
    setQuantities(newQuantities);
  };
  const renderImage = (imageData) => {
    console.log(imageData);
    const imageUrl = `data:image/jpeg;base64,${imageData}`;
    return <img src={imageUrl} alt="Product" style={{ maxWidth: "100px", maxHeight: "100px" }} />;
  };


  return (
    <div>
      <div className="bg-secondary border-top p-4 text-white mb-3">
        <h1 className="display-6 text-center">Shopping Cart</h1>
      </div>
      <div className="container">
        <div className="row">
          <div className="col-lg-9">
            <div className="card" style={{ borderColor: "black" }}>
              <div className="table-responsive">
                <table className="table table-borderless">
                  <thead className="text-muted table-responsive">
                    <tr className="small text-uppercase">
                      <th scope="col">Product Details</th>
                      <th scope="col" width={120}>
                        Quantity
                      </th>
                      <th scope="col" width={150} style={{ paddingLeft: "25px" }}>
                        Price
                      </th>
                      <th scope="col" className="text-end" width={130}></th>
                    </tr>
                  </thead>
                  <tbody>
                    {cart.map((item, index) => (
                      <tr key={index}>
                        <td>
                          <div className="row">
                            <div className="col-3 d-none d-md-block">
                              {renderImage(prods[index]?.image)}
                            </div>
                            <div className="col">
                              <Link
                                to={`/product/detail/${item.productId}`}
                                className="text-decoration-none"
                                style={{ color: "black" }}
                              >
                                <strong>{item.productName}</strong>
                              </Link>
                              <p className="small text-muted">
                                Size: {item.size}
                              </p>
                            </div>
                          </div>
                        </td>
                        <td>
                          <div className="input-group input-group-sm ">
                            <button
                              style={{ backgroundColor: "black", color: "white", borderColor: "black" }}
                              className="btn btn-primary text-white"
                              type="button"
                              onClick={() => decreaseQuantity(index)}
                            >
                              <i className="bi bi-dash-lg"></i>
                            </button>
                            <input
                              type="text"
                              className="form-control text-center"
                              value={item.quantity}
                              readOnly
                            />
                            <button
                              style={{ backgroundColor: "black", color: "white", borderColor: "black" }}
                              className="btn btn-primary text-white"
                              type="button"
                              onClick={() => increaseQuantity(index)}
                            >
                              <i className="bi bi-plus-lg"></i>
                            </button>
                          </div>
                        </td>
                        <td>
                          <var className="price" style={{ paddingLeft: "25px" }}>₹{item.price}</var>
                        </td>
                        <td className="text-end">
                          <button className="btn btn-sm btn-outline-danger">
                            <i className="bi bi-trash"></i>
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>&nbsp;
          </div><br></br>
          <div className="col-lg-3">
            <div className="card" style={{ borderColor: "black" }}>
              <div className="card-body">
                <dl className="row border-bottom">
                  <dt className="col-6">Price Details</dt>
                </dl>
                {cart.map((item, index) => (
                  <dl key={index} className="row">
                    <p className="col-6">{item.productName}</p>
                    <dd className="col-6 text-end">
                      <strong>₹{item.price * item.quantity}</strong>
                    </dd>
                  </dl>
                ))}
                <hr />
                <dl className="row">
                  <p className="col-6">Total Price</p>
                  <dd className="col-6 text-end">
                    <strong>
                      ₹
                      {cart.reduce(
                        (total, item) => total + item.price * item.quantity,
                        0
                      )}
                    </strong>
                  </dd>
                </dl>
              </div>
            </div>
          </div>
        </div>
        <div className="card-footer">
          <div className="container">
            <div className="row" style={{marginTop:'-40px'}}>
              <div className="col-md-2" >
                <Link to="/" style={{ backgroundColor: 'black', borderColor: 'black' }} className="btn btn-secondary w-100">
                  <i className="bi bi-chevron-left"></i> Shopping
                </Link>&nbsp;&nbsp;&nbsp;&nbsp;
