import { Link } from "react-router-dom";
const CardProductList = (props) => {
  const product = props.data;
  const renderImage = (imageData) => {
    const imageUrl = `data:image/jpeg;base64,${imageData}`;
    return <img src={imageUrl} alt="Product" style={{ width: '100px' }} />;
  };
  return (
    <div className="card" style={{ borderColor: "black" }}>
      <div className="row g-0">
        <div className="col-lg-3 col-md-4 col-sm-6 col-10 text-center">
          <Link to="/product/detail">
            <img
              src={renderImage(product.image)}
              alt="..."
              style={{
                width: "100%",
                maxWidth: "276px",
                height: "auto",
                padding: "20px",
              }}
            />
          </Link>
        </div>
        <div className="col-lg-6 col-md-8 col-sm-6 col-12">
          <div className="card-body">
            <h6 className="card-subtitle me-2 d-inline">
              <Link
                to={product.link}
                className="text-decoration-none"
                style={{ color: "black", fontWeight: "bold" }}
              >
                {product.name}
              </Link>
            </h6>
            {product.pdesc &&
            product.pdesc.includes("|") === false ? (
              <Link
                to="/product/detail"
                className="text-decoration-none mt-15"
                style={{
                  textAlign: "justify",
                  color: "black",
                  fontSize: "18px",
                }}
              >
                <p className="small mt-2">{product.pdesc}</p>
              </Link>
            ) : (
              <ul className="mt-2">
                {product.description.split("|").map((desc, idx) => (
                  <li key={idx}>{desc}</li>
                ))}
              </ul>
            )}
          </div>
        </div>
        <div className="col-lg-3 col-md-12 col-sm-12 col-12" style={{ marginTop: "50px" }}>
          <div className="card-body">
            <div className="mb-2">
              <span className="fw-bold h5">₹{product.price}</span>
              {product.originPrice > 0 && (
                <del className="small text-muted ms-2">
                  ₹{product.originPrice}
                </del>
              )}
              {(product.discountPercentage > 0 ||
                product.discountPrice > 0) && (
                <span
                  className={`rounded p-1 bg-dark ms-2 small`}
                  style={{ color: "white" }}
                >
                  -
                  {product.discountPercentage > 0
                    ? product.discountPercentage + "%"
                    : "₹" + product.discountPrice}
                </span>
              )}
            </div>
            {product.isFreeShipping && (
              <p className="text-success small mb-2">
                <i className="bi bi-truck" /> Free Shipping
              </p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};
export default CardProductList;
