import React, { useState } from 'react';
import { Link } from "react-router-dom";
const AddProduct = () => {
    const [name, setName] = useState('');
    const [id, setId] = useState('');
    const [size, setSize] = useState([]);
    const [pdesc, setPdesc] = useState('');
    const [image, setImage] = useState(null);
    const [originalPrice, setOriginalPrice] = useState('');
    const [discountedPrice, setDiscountedPrice] = useState('');
    const [discountPercentage, setDiscountPercentage] = useState('');
    const [category, setCategory] = useState('');
    const [stock, setStock] = useState(0);
    const handleSizeChange = (e) => {
        const { value, checked } = e.target;
        if (checked) {
            setSize([...size, value]);
        } else {
            setSize(size.filter((s) => s !== value));
        }
    };
    const handleSubmit = (event) => {
        event.preventDefault();
        console.log('Form submitted');
    };
    return (
        <div className="container">
             <button className="btn btn-dark btn-block float-end" style={{marginTop:'55px'}}>
                <Link to="/account/signinadmin" style={{color : "white", textDecoration : "none", fontSize : "18px"}}>
                  Logout
                </Link>
              </button>
            <div className="row align-items-center mb-5">
                <div className="col-lg-8 mb-4 mb-lg-0">
                    <div className="d-flex align-items-center">
                        <div>
                            <h1 style={{ marginTop: '54px', marginLeft: '16px' }}>ADD PRODUCT</h1>
                        </div>
                    </div>
                </div>
            </div>
            <div className="row mb-5" style={{ marginTop: '-15px' }}>
                <div className="col-lg-12">
                    <form onSubmit={handleSubmit} className="p-4 p-md-4 border rounded">
                        <h3 className="text-black mb-5 border-bottom pb-4">Product Details</h3>
                        <div className="form-group" style={{ marginTop: '-30px' }}>
                            <label htmlFor="name" style={{marginBottom:'6px'}}>Product Name</label>
                            <input type="text" className="form-control" id="name" name="name" value={name} onChange={(e) => setName(e.target.value)} placeholder="Write name of the product" required/>
                        </div>
                        <div className="form-group" style={{ marginTop: '15px' }}>
                            <label htmlFor="product-id" style={{marginBottom:'6px'}}>Product Id</label>
                            <input type="text" className="form-control" id="product-id" name="id" value={id} onChange={(e) => setId(e.target.value)} placeholder="Write the Product Id" required />
                        </div>
                        <div className="form-group" style={{ marginTop: '15px' }}>
                            <label htmlFor="product-description" style={{marginBottom:'6px'}}>Product Description</label>
                            <textarea name="pdesc" id="pdesc" cols="30" rows="10" className="form-control" value={pdesc} onChange={(e) => setPdesc(e.target.value)} placeholder="Write the product description here..." required></textarea>
                        </div>
                        <div className="form-group" style={{ marginTop: '15px' }}>
                            <label htmlFor="original-price" style={{marginBottom:'6px'}}>Original Price</label>
                            <input type="text" className="form-control" id="original-price" name="originalPrice" placeholder='Write the original price of the product' value={originalPrice} onChange={(e) => setOriginalPrice(e.target.value)} required />
                        </div>
                        <div className="form-group" style={{ marginTop: '15px' }}>
                            <label htmlFor="discounted-price" style={{marginBottom:'6px'}}>Discounted Price</label>
                            <input type="text" className="form-control" id="discounted-price" name="discountedPrice" placeholder='Write the price of the product after discount' value={discountedPrice} onChange={(e) => setDiscountedPrice(e.target.value)} required/>
                        </div>
                        <div className="form-group" style={{ marginTop: '15px' }}>
                            <label htmlFor="discount-percentage" style={{marginBottom:'6px'}}>Discount Percentage ( Without % Sign )</label>
                            <input type="text" className="form-control" id="discount-percentage" name="discountPercentage" placeholder='Write the discount percentage' value={discountPercentage} onChange={(e) => setDiscountPercentage(e.target.value)} required/>
                        </div>
                        <div className="form-group" style={{ marginTop: '15px' }}>
                            <label htmlFor="category" style={{ marginBottom: '5px' }}>Category</label><br />
                            <label><input type="radio" name="category" value="Men" onChange={(e) => setCategory(e.target.value)} checked={category === 'Men'} /> Men</label>&nbsp;&nbsp;&nbsp;&nbsp;
                            <label><input type="radio" name="category" value="Women" onChange={(e) => setCategory(e.target.value)} checked={category === 'Women'} /> Women</label>&nbsp;&nbsp;&nbsp;&nbsp;
                            <label><input type="radio" name="category" value="Kids" onChange={(e) => setCategory(e.target.value)} checked={category === 'Kids'} /> Kids</label>&nbsp;&nbsp;&nbsp;&nbsp;
                        </div>
                        <div className="form-group" style={{ marginTop: '15px' }}>
                        <label style={{ marginBottom: '5px' }}>Size</label><br />
                            <label className="custom-checkbox" style={{ fontSize: '20px' }}><input type="checkbox" value="6C" onChange={handleSizeChange}  style={{ width: '18px', height: '18px' }} /> 6C</label>&nbsp;&nbsp;&nbsp;&nbsp;
                            <label className="custom-checkbox" style={{ fontSize: '20px' }}><input type="checkbox" value="7C" onChange={handleSizeChange}  style={{ width: '18px', height: '18px' }} /> 7C</label>&nbsp;&nbsp;&nbsp;&nbsp;      
                            <label className="custom-checkbox" style={{ fontSize: '20px' }}><input type="checkbox" value="8C" onChange={handleSizeChange}  style={{ width: '18px', height: '18px' }} /> 8C</label>&nbsp;&nbsp;&nbsp;&nbsp;         
                            <label className="custom-checkbox" style={{ fontSize: '20px' }}><input type="checkbox" value="9C" onChange={handleSizeChange}  style={{ width: '18px', height: '18px' }} /> 9C</label>&nbsp;&nbsp;&nbsp;&nbsp;            
                            <label className="custom-checkbox" style={{ fontSize: '20px' }}><input type="checkbox" value="10C" onChange={handleSizeChange}  style={{ width: '18px', height: '18px' }} /> 10C</label>&nbsp;&nbsp;&nbsp;&nbsp;
                            <label className="custom-checkbox" style={{ fontSize: '20px' }}><input type="checkbox" value="1" onChange={handleSizeChange}  style={{ width: '18px', height: '18px' }} /> 1</label>&nbsp;&nbsp;&nbsp;&nbsp;                         
                            <label className="custom-checkbox" style={{ fontSize: '20px' }}><input type="checkbox" value="2" onChange={handleSizeChange}  style={{ width: '18px', height: '18px' }} /> 2</label>&nbsp;&nbsp;&nbsp;&nbsp;                          
                            <label className="custom-checkbox" style={{ fontSize: '20px' }}><input type="checkbox" value="3" onChange={handleSizeChange}  style={{ width: '18px', height: '18px' }} /> 3</label>&nbsp;&nbsp;&nbsp;&nbsp;                          
                            <label className="custom-checkbox" style={{ fontSize: '20px' }}><input type="checkbox" value="4" onChange={handleSizeChange}  style={{ width: '18px', height: '18px' }} /> 4</label>&nbsp;&nbsp;&nbsp;&nbsp;
                            <label className="custom-checkbox" style={{ fontSize: '20px' }}><input type="checkbox" value="5" onChange={handleSizeChange}  style={{ width: '18px', height: '18px' }} /> 5</label>&nbsp;&nbsp;&nbsp;&nbsp;
                            <label className="custom-checkbox" style={{ fontSize: '20px' }}><input type="checkbox" value="6" onChange={handleSizeChange}  style={{ width: '18px', height: '18px' }} /> 6</label>&nbsp;&nbsp;&nbsp;&nbsp;
                            <label className="custom-checkbox" style={{ fontSize: '20px' }}><input type="checkbox" value="7" onChange={handleSizeChange}  style={{ width: '18px', height: '18px' }} /> 7</label>&nbsp;&nbsp;&nbsp;&nbsp;
                            <label className="custom-checkbox" style={{ fontSize: '20px' }}><input type="checkbox" value="8" onChange={handleSizeChange}  style={{ width: '18px', height: '18px' }} /> 8</label>&nbsp;&nbsp;&nbsp;&nbsp;
                            <label className="custom-checkbox" style={{ fontSize: '20px' }}><input type="checkbox" value="8" onChange={handleSizeChange} style={{ width: '18px', height: '18px' }} /> 9</label>&nbsp;&nbsp;&nbsp;&nbsp;
                            <label className="custom-checkbox" style={{ fontSize: '20px' }}><input type="checkbox" value="8" onChange={handleSizeChange} style={{ width: '18px', height: '18px' }} /> 10</label><br />
                        </div>
                        <div className="form-group" style={{ marginTop: '15px' }}>
                            <label htmlFor="stock" style={{marginBottom:'6px'}}>Stock</label>
                            <input type="number" className="form-control" id="stock" name="stock" value={stock} onChange={(e) => setStock(e.target.value)} required/>
                        </div>
                        <div className="form-group" style={{ marginTop: '15px' }}>
                            <label htmlFor="product-image" style={{marginBottom:'6px'}}>Add Product Image</label><br />
                            <input type="file" name="image" id="logo" accept="image/jpeg, image/png, image/jpg, image/gif" onChange={(e) => setImage(e.target.files[0])} required/>
                        </div>
                        <div className="row align-items-center mb-5">
                            <div className="col-lg-4 ml-auto">
                                <div className="row">
                                    <div className="col-6"style={{marginTop:'30px', marginBottom:'-20px'}}>
                                        <input type="submit" value="Add Product" className="btn btn-primary btn-md text-white" style={{backgroundColor:'black', borderColor:'black'}}/>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};
export default AddProduct;
