import { Link } from "react-router-dom";

const CardProductGrid = (props) => {
  const product = props.data;
  const renderImage = (imageData) => {
    const imageUrl = `data:image/jpeg;base64,${imageData}`;
    return <img src={imageUrl} alt="Product" style={{ width: '100px' }} />;
  };
  return (
    <div className="card" style={{width:'340px', height:'320px', borderColor : "black"}}>
      <Link to="/product/detail" > {renderImage(product.image)}</Link>

      {product.discountPercentage > 0  && (
        <span
          className={`rounded position-absolute p-2 bg-dark  ms-2 small ${
            product.isNew ? "mt-5" : "mt-2"
          }`}
        style={{color:'white'}}>
          -
          {product.discountPercentage > 0
            ? product.discountPercentage + "%"
            : "₹" + product.discountedPrice}
        </span>
      )}
      <div className="card-body">
        <h6 className="card-subtitle mb-2">
          <Link to="/product/detail" className="text-decoration-none" style={{color:'black'}}>
            {product.name}
          </Link>
        </h6>
        <div className="my-2">
          <span className="fw-bold h5">₹{product.discountedPrice}</span>
          {product.originPrice > 0 && (
            <del className="small text-muted ms-2">₹{product.originalPrice}</del>
          )}

        </div>
        <div className="btn-group  d-flex" role="group">
              {/* <button
                type="button"
                className="btn btn-sm btn-outline-danger"
                title="Add to wishlist"
                style={{borderColor:'red', marginTop:'5px'}}
              >
                <i className="bi bi-heart" />
              </button> */}
        </div>
      </div>
    </div>
  );
};

export default CardProductGrid;
