            console.log('Error while calling contactUs Api ', error.message);
            throw new Error('Invalid email address');
        } else {
            console.log('Error while calling contactUs Api ', error.message);
            throw new Error('Failed to send message. Please try again later.');
        }
    }
}

export const addProduct = async (product) => {
