import React, { lazy, useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import { fetchOrders } from "../../api.js"; // Assuming you have a function to fetch order items
const Breadcrumb = lazy(() => import("../../components/Breadcrumb4"));

function OrderList() {
  const [orders, setOrders] = useState([]);
  
  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const ordersData = await fetchOrders();
      const orderItemsData = await fetchOrderItems();

      // Map order items to their respective orders based on order_id
      const ordersWithItems = ordersData.map(order => ({
        ...order,
        items: orderItemsData.filter(item => item.order_id === order.id)
      }));

      setOrders(ordersWithItems);
    } catch (error) {
      console.error('Error fetching data: ', error);
    }
  };

  const handleButtonClick = (id, status) => {
    // Handle button click logic
  };

  const handleLogout = () => {
    localStorage.removeItem("value");
  };

  return (
    <div>
      {/* Header */}
      <div className="bg-secondary border-top p-3 p-md-5 text-white mb-3">
        <div className="container-fluid">
          <div className="row align-items-center">
            <div className="col-md-3">
              <Link to="/">
                <img alt="logo" src="../../images/(0).png" style={{ height: "44px", width: "159px" }} />
              </Link>
            </div>
            <div className="col-md-6 text-center">
              <h1 className="display-6">Manage Orders</h1>
            </div>
            <div className="col-md-3 text-end">
              <Link to="/account/signinadmin" className="text-white" style={{ color: "white", textDecoration: "none", fontSize: "18px" }}>
                <button className="btn btn-dark" onClick={handleLogout}> Logout</button>
              </Link>
            </div>
          </div>
        </div>
      </div>
      
      {/* Breadcrumb */}
      <div style={{ marginTop: "-16px", display: "flex" }}>
        <Breadcrumb />
        <span style={{ marginTop: "9px", marginLeft: "-10px" }}>/ Manage Orders</span>
      </div>
      
      {/* Orders table */}
      <div style={{ padding: "40px" }}>
        <div className="table-responsive">
          <table className="table table-striped table-bordered table-hover table-condensed" width="80%" align="center">
            <thead>
              <tr>
                <th style={{ backgroundColor: "black", color: "white" }}><center>Order ID</center></th>
                <th style={{ backgroundColor: "black", color: "white" }}><center>Order Date</center></th>
                <th style={{ backgroundColor: "black", color: "white" }}><center>Product Name</center></th>
                <th style={{ backgroundColor: "black", color: "white" }}><center>Product Price</center></th>
                <th style={{ backgroundColor: "black", color: "white" }}><center>Shipping Address</center></th>
                <th style={{ backgroundColor: "black", color: "white" }}><center>Shipping Status</center></th>
              </tr>
            </thead>
            <tbody>
              {orders.map(order => (
                <React.Fragment key={order.id}>
                  {/* Order row */}
                  <tr>
                    <td><center>{order.id}</center></td>
                    <td><center>{order.date}</center></td>
                    <td colSpan="4"></td> {/* Empty columns for alignment */}
                  </tr>
                  {/* Items */}
                  {order.items.map(item => (
                    <tr key={item.id}>
                      <td colSpan="2"></td> {/* Empty columns for alignment */}
                      <td><center>{item.productName}</center></td>
                      <td><center>{item.productPrice}</center></td>
                      <td><center>{item.shippingAddress}</center></td>
                      <td>
                        <center>
                          {item.shippingStatus}
                          <button onClick={() => handleButtonClick(order.id, 'Order Confirmed')} className="btn btn-primary btn-md text-white" style={{ backgroundColor: 'black', margin: '10px', borderColor: 'black' }}>Order Confirmed</button>
                          <button onClick={() => handleButtonClick(order.id, 'Shipped')} className="btn btn-primary btn-md text-white" style={{ backgroundColor: 'black', marginRight: '10px', borderColor: 'black' }}>Shipped</button>
                          <button onClick={() => handleButtonClick(order.id, 'Out for Delivery')} className="btn btn-primary btn-md text-white" style={{ backgroundColor: 'black', marginRight: '10px', borderColor: 'black' }}>Out for Delivery</button>
                          <button onClick={() => handleButtonClick(order.id, 'Delivered')} className="btn btn-primary btn-md text-white" style={{ backgroundColor: 'black', borderColor: 'black' }}>Delivered</button>
                        </center>
                      </td>
                    </tr>
                  ))}
                </React.Fragment>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default OrderList;
