import React, { useState, useEffect } from "react";
import { Field, reduxForm } from "redux-form";
import { compose } from "redux";
import { connect } from "react-redux"; 
import renderFormGroupField from "../../helpers/renderFormGroupField";
import {
  required,
  maxLengthMobileNo,
  minLengthMobileNo,
  digit,
  name,
  email,
} from "../../helpers/validation";
import { ReactComponent as IconPerson } from "bootstrap-icons/icons/person.svg";
import { ReactComponent as IconEnvelop } from "bootstrap-icons/icons/envelope.svg";
import { getCustomerData } from "../../api.js";

const ProfileForm = (props) => {
  const {
    handleSubmit,
    submitting,
    onSubmit,
    submitFailed,
    initialValues,
  } = props;

  const [customerData, setCustomerData] = useState({
    email: "",
    phno: "",
    name: ""
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const userData = localStorage.getItem('data');
      const customerInfo = await getCustomerData(userData);
      console.log("Customer Data : ", customerInfo);
      setCustomerData(customerInfo);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  return (
    <form
      onSubmit={handleSubmit(onSubmit)}
      className={`needs-validation ${submitFailed ? "was-validated" : ""}`}
      noValidate
    >
      <div className="card" style={{ paddingBottom: "5px", borderColor: "black" }}>
        <h5 className="card-header text-white" style={{ backgroundColor: "black" }}>
          <i className="bi bi-person-lines-fill" /> &nbsp;Profile Detail
        </h5>
        <ul className="list-group list-group-flush">
          <li className="list-group-item" >
            <Field
              name="email"
              type="email"
              label="Email"
              component={renderFormGroupField}
              placeholder="Email"
              icon={IconEnvelop}
              validate={[required, email]}
              required={true}
            />
          </li>
          <li className="list-group-item" >
            <Field
              name="name"
              type="text"
              label="Name"
              component={renderFormGroupField}
              placeholder="Name"
              icon={IconPerson}
              validate={[required, name]}
              required={true}
            />
          </li>
          <li className="list-group-item" >
            <Field
              name="mobileNo"
              type="text"
              label="Phone no."
              component={renderFormGroupField}
              placeholder="Mobile no without country code"
              validate={[required, maxLengthMobileNo, minLengthMobileNo, digit]}
              required={true}
              max="999999999999999"
              min="9999"
            />
          </li>
        </ul>
        <div className="card-body" style={{ marginTop: '12px' }}>
          <button
            type="submit"
            className="btn btn-primary  d-flex"
            disabled={submitting}
            style={{ background: "black", borderColor: "black" }}
          >
            Save
          </button>
        </div>
      </div>
    </form>
  );
};

const mapStateToProps = (state, ownProps) => {
  return {
    initialValues: ownProps.customerData,
  };
};

export default compose(
  reduxForm({
    form: "profile",
    enableReinitialize: true,
  }),
  connect(mapStateToProps)
)(ProfileForm);
