import React, { useState } from "react";
import { Field, reduxForm } from "redux-form";
import { compose } from "redux";
import { Link } from "react-router-dom";
import renderFormGroupField from "../../helpers/renderFormGroupField";
import { required, maxLength20, minLength8 } from "../../helpers/validation";
import { getSignin } from "../../api.js";
import { useParams } from "react-router-dom";
// import { Navigate } from "react-router-dom";
import { useNavigate } from 'react-router-dom';
const initialValues = {
  email: '',
  password: '',
}
const SignInForm = (props) => {
  const [signin, setSignin] = useState(initialValues)
  const navigate = useNavigate();
  const usegetCustRecord = async (email) => {
    let response = await getSignin(email);
    console.log(response);
    setSignin(response.data);
    var cemail = signin.email;
    if (cemail !== "") {
      localStorage.setItem("data", cemail)
      navigate('/', { replace: true });
    }
    else {
      navigate('/account/signin', { replace: true });
    }
  }
  const onValueChange = (e) => {
    setSignin({ ...signin, [e.target.name]: e.target.value })
    console.log(signin)
  }
  return (
    <form
    >
      <Field
        name="email"
        label="Email"
        type="email"
        title="Please enter your valid email address"
        component={renderFormGroupField}
        placeholder="Enter valid email address"
        validate={[required]}
        required={true}
        pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$"
        className="mb-3"
        onChange={(e) => onValueChange(e)}
      />
      <Field
        name="password"
        type="password"
        label="Password"
        title="Please enter your password"
        pattern="(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,20}"
        component={renderFormGroupField}
        placeholder="Enter your password"
        validate={[required, maxLength20, minLength8]}
        required={true}
        maxLength="20"
        minLength="8"
        className="mb-3"
        onChange={(e) => onValueChange(e)}
      />
      <div className="d-grid">
        <button
          type="submit"
          className="btn btn-dark mb-3"
          style={{ background: "black", borderColor: "black" }}
          onClick={() => usegetCustRecord(signin.email)}
        >
          Sign In
        </button>
      </div>
      <Link className="float-start" to="/account/signup" title="Sign Up" style={{ color: "black" }}>
        Create account
      </Link>
      <Link className="float-end" to="/account/forgotpassword" title="Forgot Password" style={{ color: "black" }}>
        Forgot Password?
      </Link>
      <div className="clearfix"></div>
      <div className="row"></div>
    </form>
  );
};
export default compose(reduxForm({ form: "signin" }))(SignInForm);
