import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import { fetchOrderItemsData, getProduct } from "../../api.js";

const OrdersView = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const userData = localStorage.getItem('data');
      const orderItems = await fetchOrderItemsData();
      const filteredOrders = orderItems.filter(order => order.order.email === userData);
      const updatedOrder = await Promise.all(filteredOrders.map(async (order) => {
        const productDetails = await getProduct(order.productId);
        return {
          ...item,
          image: productDetails.image,
          availableQuantity: availableQuantity
        };
      }));
      setOrders(filteredOrders);
    } catch (error) {
      console.error('Error fetching orders:', error);
    }
  };

  const handleButtonClick = (orderId) => {
    console.log('Return confirmed for order:', orderId);
    // You can implement your return logic here, like updating the order status in the database
  };

  return (
    <div className="container mb-3">
      <h4 className="my-3">ORDERS</h4>
      <div className="row g-3">
        {orders.map(order => (
          <div className="col-md-6" key={order.id}>
            <div className="card" style={{ borderColor: "black" }}>
              <div className="row g-0">
                <div className="col-md-3 text-center">
                  <img
                    src={`../../images/${order.id}.png`} // Assuming each order has an associated image
                    className="img-fluid"
                    style={{ width: "320px", height: "140px", padding: "8px" }}
                    alt="..."
                  />
                </div>
                <div className="col-md-9">
                  <div className="card-header bg-white">
                    <div className="small">
                      <span className="border bg-black bg-secondary rounded-left px-2 text-white">
                        Order ID
                      </span>
                      <span className=" bg-white rounded-right px-1 me-3">
                        #{order.order.id}
                      </span>
                      <span className="border bg-black bg-secondary rounded-left px-2 text-white">
                        Date
                      </span>
                      <span className=" bg-white rounded-right px-2">
                        {order.order.date}{/* Assuming order title is the date */}
                      </span>
                    </div>
                  </div>
                  <div className="card-body">
                    <h6>
                      <Link to="/" className="text-black text-decoration-none">
                      {order.productName}  {/* Assuming order title is the product name */}
                      </Link>
                    </h6>
                    <div className="small">
                      <span className="text-muted me-2">Size:</span>
                      <span className="me-3">{order.size}</span>
                      <span className="text-muted me-2">Price:</span>
                      <span className="me-3">₹{order.price}</span> {/* Assuming price is based on order ID */}
                    </div>
                    <div className="mt-2"></div>
                  </div>
                  <div className="card-footer bg-white">
                    <span className="me-2">Status:</span>&nbsp;&nbsp;&nbsp;&nbsp;
                    <button
                      onClick={() => handleButtonClick(order.id)}
                      style={{ borderRadius: '6px', color: "black", backgroundColor: "whitesmoke" }}
                    >
                      Return
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default OrdersView;
