import React, { useState } from 'react';
import { Field, reduxForm } from "redux-form";
import { compose } from "redux";
import renderFormGroupField from "../../helpers/renderFormGroupField";
import { required, maxLength20, minLength8 } from "../../helpers/validation";
import { changePassword } from "../../api.js";
const ChangePasswordForm = (props) => {
  const { submitting, onSubmit, submitFailed } = props;
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');
  const [passwordMatchError, setPasswordMatchError] = useState('');
  const handleCurrentPasswordChange = (e) => {
    setCurrentPassword(e.target.value);
    setSuccessMessage('');
  };
  const handleNewPasswordChange = (e) => {
    setNewPassword(e.target.value);
    setSuccessMessage('');
  };
  const handleConfirmNewPasswordChange = (e) => {
    setConfirmNewPassword(e.target.value);
    setSuccessMessage('');
  };
  const handleSubmit = async (formData) => {
    const allFieldsFilled = Object.values(formData).every(value => value.trim() !== '');
    if (!allFieldsFilled) {
      return;
    }
    else if (newPassword !== confirmNewPassword) {
      setPasswordMatchError("New password doesn't match");
    } else {
      try {
        const userData = localStorage.getItem('data');
        const response = await changePassword(userData, formData.currentPassword, formData.password);
        console.log("Response : ", response);
        setSuccess("Password updated successfully!");
        setError(false);
        setTimeout(() => {
          setSuccess(false);
          props.reset();
        }, 2000);
      } catch (error) {
        console.error('Error changing password:', error.response.data);
        setPasswordMatchError(error.response.data.message);
        setSuccessMessage('');
      }
    }
  };

  return (
    <div className="card" style={{ height: "390px", borderColor: "black", marginTop: "12px", height: "390px" }}>
      <h5 className="card-header text-white" style={{ backgroundColor: "black", paddingTop: "10px", height: "44px" }}>
        <i className="bi bi-key text-white"></i> &nbsp; Change Password
      </h5>
      <div className="card-body">
        <form
          onSubmit={handleSubmit}
          className={`needs-validation ${submitFailed ? "was-validated" : ""}`}
          noValidate
        >
          <Field
            name="currentPassword"
            type="password"
            label="Current Password"
            value={currentPassword}
            onChange={handleCurrentPasswordChange}
            component={renderFormGroupField}
            placeholder="Enter your current password"
            validate={[required, maxLength20, minLength8]}
            required={true}
            maxLength="20"
            minLength="8"
            className="mb-3"
          />
          <Field
            name="password"
            type="password"
            label="New Password"
            value={newPassword}
            onChange={handleNewPasswordChange}
            component={renderFormGroupField}
            placeholder="Enter new password "
            validate={[required, maxLength20, minLength8]}
            required={true}
            maxLength="20"
            minLength="8"
            className="mb-3"
          />
          <Field
            name="confirmPassword"
            type="password"
            label="Confirm Password"
            value={confirmNewPassword}
            onChange={handleConfirmNewPasswordChange}
            component={renderFormGroupField}
            placeholder="Confirm new password"
            validate={[required, maxLength20, minLength8]}
            required={true}
            maxLength="20"
            minLength="8"
            className="mb-3"
          />
          <div className="card-body d-flex" style={{ paddingLeft: "2px", paddingTop: "13px" }} >
            <button
              type="submit"
              className="btn btn-primary  d-flex"
              disabled={submitting}
              style={{ background: "black", borderColor: "black", marginRight: "15px" }}
            >
              Save
            </button>
            {passwordMatchError && <p style={{ color: 'red' }}>{passwordMatchError}</p>}
            {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}
          </div>
        </form>
      </div>
    </div>
  );
};
export default compose(
  reduxForm({
    form: "changepassword",
  })
)(ChangePasswordForm);
