import React, { useState } from "react";
import { Field, reduxForm } from "redux-form";
import { compose } from "redux";
import { Link } from "react-router-dom";
import renderFormGroupField from "../../helpers/renderFormGroupField";
import {
  required,
  maxLength20,
  minLength8,
} from "../../helpers/validation";
const SignInAdminForm = (props) => {
  const { handleSubmit, submitting, history, submitFailed } = props;
  const [errorMessage, setErrorMessage] = useState("");

  const onSubmit = (formData) => {
    // Check if email and password match the required credentials
    if (formData.email === "sneakify05@gmail.com" && formData.password === "#Sneakify@5") {
      // Redirect to admin dashboard upon successful sign-in
      history.push("/account/admindash");
    } else {
      // Set error message for incorrect credentials
      setErrorMessage("Incorrect Email or Password");
    }
  };

  return (
    <form
      onSubmit={handleSubmit(onSubmit)}
      className={`needs-validation ${submitFailed ? "was-validated" : ""}`}
      noValidate
    >
      <Field
        name="email"
        label="Email"
        type = "email"
        title="Please enter your valid email address"
        component={renderFormGroupField}
        placeholder="Enter valid email address"
        validate={[required]}
        required={true}
        pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$"
        className="mb-3"
      />
      <Field
        name="password"
        type="password"
        label="Password"
        title="Please enter your password"
        pattern="(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,20}"
        component={renderFormGroupField}
        placeholder="Enter your password"
        validate={[required, maxLength20, minLength8]}
        required={true}
        maxLength="20"
        minLength="8"
        className="mb-3"
      />
       {errorMessage && <div className="text-danger">{errorMessage}</div>}
      <div className="d-grid"><br></br>
      <Link  to ="#">
        <button
          type="submit"
          className="btn btn-dark mb-3"
          disabled={submitting}
          style={{background : "black", borderColor : "black"}}
        >
          Sign In
        </button></Link>
      </div>
      <div className="clearfix"></div>
      <div className="row">
      </div>
    </form>
  );
};
export default compose(
  reduxForm({
    form: "signin",
  })
)(SignInAdminForm);
