import React, { useState, useEffect } from "react";
import { reduxForm } from "redux-form";
import { compose } from "redux";
import {
  required,
  maxLengthMobileNo,
  minLengthMobileNo,
  digit
} from "../../helpers/validation";
import { getCustomerData } from "../../api.js";
const ProfileForm = (props) => {
  const {
    handleSubmit,
    submitting,
    submitFailed,
  } = props;
  const [customerData, setCustomerData] = useState({
    email: "",
    phno: "",
    name: ""
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const userData = localStorage.getItem('data');
      const customerInfo = await getCustomerData(userData);
      console.log("Customer Data : ", customerInfo);
      setCustomerData(customerInfo);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className={`needs-validation ${submitFailed ? "was-validated" : ""}`}
      noValidate
    >
      <div className="card" style={{ paddingBottom: "5px", borderColor: "black" }}>
        <h5 className="card-header text-white" style={{ backgroundColor: "black" }}>
          <i className="bi bi-person-lines-fill" /> &nbsp;Profile Detail
        </h5>
        <ul className="list-group list-group-flush">
          <li className="list-group-item" >
            <label htmlFor="email" style={{ marginLeft: '2px', marginBottom: '10px' }}>Email:</label>
            <input
              type="email"
              className="form-control"
              placeholder="Registered email address"
              aria-label="Email Address"
              value={customerData.email}
              onChange={(e) => setCustomerData(prevState => ({ ...prevState, email: e.target.value }))}
              validate={[required]}
              required={true}
              pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$"
              title="Please enter your valid email address"
              disabled
            />
          </li>
          <li className="list-group-item" >
          <label htmlFor="name" style={{ marginLeft: '2px', marginBottom: '10px' }}>Name:</label>
            <input
              type="text"
              className="form-control"
              placeholder="Name"
              value={customerData.name}
              onChange={(e) => setCustomerData(prevState => ({ ...prevState, name: e.target.value }))}
              required
            />
          </li>
          <li className="list-group-item" >
            <label htmlFor="mobile" style={{ marginLeft: '2px', marginBottom: '10px' }}>Mobile No.:</label>
            <input
              type="tel"
              className="form-control"
              placeholder="Mobile no. without country code"
              aria-label="Mobile no"
              value={customerData.phno}
              onChange={(e) => setCustomerData(prevState => ({ ...prevState, phno: e.target.value }))}
              pattern="^[0-9]{10}$"
              title="Please enter your valid mobile number"
              validate={[required, maxLengthMobileNo, minLengthMobileNo, digit]}
              required={true}
              max="9999999999"
              min="9999999999"
            />
          </li>


        </ul>
        <div className="card-body" style={{ marginTop: '12px' }}>
          <button
            type="submit"
            className="btn btn-primary  d-flex"
            disabled={submitting}
            style={{ background: "black", borderColor: "black" }}
          >
            Save
          </button>
        </div>
      </div>
    </form>
  );
};
export default compose(
  reduxForm({
    form: "profile",
  })
)(ProfileForm);
